import axios from "axios";
import { BASE_URL } from "../BASE_URL";
import Swal from "sweetalert2";

export const REGISTER = "REGISTER";
export const LOGIN = "LOGIN";
export const GET_LIST_USER = "GET LIST USER";

export const getListUser = () => {
  return (dispatch) => {
    // loading
    dispatch({
      type: GET_LIST_USER,
      payload: {
        loading: true,
        data: false,
        errorMessage: false,
      },
    });

    // getAPI
    axios({
      method: "GET",
      url: `${BASE_URL}/users`,
      timeout: 10000,
    })
      .then((res) => {
        dispatch({
          type: GET_LIST_USER,
          payload: {
            loading: false,
            data: res.data,
            errorMessage: false,
          },
        });
      })
      .catch((err) => {
        dispatch({
          type: GET_LIST_USER,
          payload: {
            loading: false,
            data: false,
            errorMessage: err,
          },
        });
      });
  };
};

export const register = (data) => {
  return (dispatch) => {
    // loading
    dispatch({
      type: REGISTER,
      payload: {
        loading: true,
        data: false,
        errorMessage: false,
      },
    });

    // getAPI
    axios({
      method: "POST",
      url: `${BASE_URL}/register`,
      data: data,
      timeout: 10000,
    })
      .then((res) => {
        Swal.fire("Success!", "Data submitted sucessfully", "success").then(
          (move) => {
            dispatch({
              type: REGISTER,
              payload: {
                loading: false,
                data: res.data,
                errorMessage: false,
              },
            });
            getListUser()
          }
        );
      })
      .catch((err) => {
        dispatch({
          type: REGISTER,
          payload: {
            loading: false,
            data: false,
            errorMessage: err,
          },
        });
      });
  };
};
